import React, { useState } from 'react'
import MoreVertIcon from '@material-ui/icons/MoreVert';
import InsertEmoticonIcon from '@material-ui/icons/InsertEmoticon';
import MicIcon from '@material-ui/icons/Mic';
import { Avatar, IconButton } from '@material-ui/core';
import { AttachFile, SearchOutlined } from '@material-ui/icons';
import InsertDriveFileIcon from '@material-ui/icons/InsertDriveFile';
import "./chat.scss"
import axios from '../../axios';

function Chat({messages}) {


    const[input,setInput] = useState("")

    const sendMessage = async(e) => {
        e.preventDefault();

        await axios.post("/messages/new", {
            message: input,
            name: "Demo",
            timestamp: "Just now",
            received: true,
        });

        setInput("");

    }


  return (
    <div className='chat'>
        <div className="chat_header">
            <div className="chat_header_info">
                <h3> Name</h3>
            </div>

            <div className="chat_header_right">
                <IconButton>
                    <SearchOutlined />
                </IconButton>

                <IconButton>
                    <AttachFile />
                </IconButton>

                <IconButton>
                    <MoreVertIcon />
                </IconButton>
            </div>
        </div>

        <div className="chat_body">

            {messages.map((message) => (
            <div className={`chat_message ${message.received && 'chat_receiver'}`}>
            <span className='chat_name'>{message.name}</span>
            {message.message}
            
            <span className='chat_time'>
                {message.timestamp}
            </span>
        </div>))}
        </div>

        <div className="chat_foot">
            <div className="chat_foot_top">
                <div className="chat_foot_top_left">
                <InsertEmoticonIcon />
                <MicIcon />

                </div>

                <div className="chat_foot_top_right">
                <MicIcon />


                </div>




            </div>

            <form className='chat_foot_textbox'>
            <textarea
                value= {input}
                onChange = {(e) => setInput(e.target.value)}

                type="text" />

                <button onClick={sendMessage} type = "submit">
                    Send
                </button>
            </form>



        </div>
    </div>
  )
}

export default Chat
