const AUthReducer = (state, action) =>{
    switch(action.type){
        case "LOGIN_START":
            return{
                user:null,
                isFecthing: true,
                error:false,
            };


        case "LOGIN_SUCCESS":
            return{
                user:action.package,
                isFecthing: false,
                error:false,
            };


        case "LOGIN_FAILURE":
            return{
                user:null,
                isFecthing: false,
                error:action.package,
            };
            

        default: 
            return state;
    }

}

export default AUthReducer;
